library(shiny)

Warning: package 'shiny' was built under R version 4.3.3

Questions &lt;- c("Identify priority areas for biodiversity conservation",
                "Evaluate multiple policy or management options",
                "Monitor status and trends in biodiversity")

Facets &lt;- c("Biodiversity",  "ecosystem services", "physical features")
Facets_html &lt;- c(href='/Biodiversity.html',href='/Planning_form.html',href='/Planning_form.html',href='/Planning_form.html')

Models &lt;- c("Data observation", "Building indicators", "Trend detection", "Attribution", "Projection", "Mitigation")

# Define UI ----
ui &lt;- fluidPage(
  title = 'Selectize examples',
  sidebarLayout(
    sidebarPanel(
      selectInput(
        'e0', '0. Main question you ight to answer', choices = Questions,
        selectize = FALSE
      ),
      selectizeInput(
        'e1', '1. Facets you might to test',
        choices = Facets
      ),
      selectizeInput(
        'e2', '2. Multi-select', choices = state.name, multiple = TRUE
      ),
      selectizeInput(
        'e3', '3. Item creation', choices = state.name,
        options = list(create = TRUE)
      ),
      selectizeInput(
        'e4', '4. Max number of options to show', choices = state.name,
        options = list(maxOptions = 5)
      ),
      selectizeInput(
        'e5', '5. Max number of items to select', choices = state.name,
        multiple = TRUE, options = list(maxItems = 2)
      ),
      # I() indicates it is raw JavaScript code that should be evaluated, instead
      # of a normal character string
      selectizeInput(
        'e6', '6. Placeholder', choices = state.name,
        options = list(
          placeholder = 'Please select an option below',
          onInitialize = I('function() { this.setValue(""); }')
        )
      ),
      selectInput(
        'e7', '7. selectInput() does not respond to empty strings',
        choices = state.name
      )
    ),
    mainPanel(
      helpText('Output of the examples in the left:'),
       uiOutput("link"),
      verbatimTextOutput('ex_out'),
      # use Github instead
      selectizeInput('github', 'Select a Github repo', choices = '', options = list(
        valueField = 'url',
        labelField = 'name',
        searchField = 'name',
        options = list(),
        create = FALSE,
        render = I("{
      option: function(item, escape) {
        return '&lt;div&gt;' +
               '&lt;strong&gt;&lt;img src=\"http://selectize.github.io/selectize.js/images/repo-' + (item.fork ? 'forked' : 'source') + '.png\" width=20 /&gt;' + escape(item.name) + '&lt;/strong&gt;:' +
               ' &lt;em&gt;' + escape(item.description) + '&lt;/em&gt;' +
               ' (by ' + escape(item.username) + ')' +
            '&lt;ul&gt;' +
                (item.language ? '&lt;li&gt;' + escape(item.language) + '&lt;/li&gt;' : '') +
                '&lt;li&gt;&lt;span&gt;' + escape(item.watchers) + '&lt;/span&gt; watchers&lt;/li&gt;' +
                '&lt;li&gt;&lt;span&gt;' + escape(item.forks) + '&lt;/span&gt; forks&lt;/li&gt;' +
            '&lt;/ul&gt;' +
        '&lt;/div&gt;';
      }
    }"),
        score = I("function(search) {
      var score = this.getScoreFunction(search);
      return function(item) {
        return score(item) * (1 + Math.min(item.watchers / 100, 1));
      };
    }"),
        load = I("function(query, callback) {
      if (!query.length) return callback();
      $.ajax({
        url: 'https://api.github.com/legacy/repos/search/' + encodeURIComponent(query),
        type: 'GET',
        error: function() {
          callback();
        },
        success: function(res) {
          callback(res.repositories.slice(0, 10));
        }
      });
    }")
      )),
      helpText('If the above searching fails, it is probably the Github API limit
                 has been reached (5 per minute). You can try later.'),
      verbatimTextOutput('github')
    )
  )
)

# Define server logic ----
server &lt;- function(input, output) {
  #output$link &lt;- renderUI(a(input$'e1', href = "https://shiny.rstudio.com"))



  output$ex_out &lt;- renderPrint({
    str(sapply(sprintf('e%d', 0:7), function(id) {
     input[[id]]
    }, simplify = FALSE))
  })



  output$github &lt;- renderText({
    paste('You selected', if (input$github == '') 'nothing' else input$github,
          'in the Github example.')
  })
}

# Run the app ----
shinyApp(ui = ui, server = server)


