## Only run examples in interactive R sessions
if (interactive()) {

# basic example
shinyApp(
  ui = fluidPage(
    selectInput("variable", "Variable:",
                c("Cylinders" = "cyl",
                  "Transmission" = "am",
                  "Gears" = "gear")),
    tableOutput("data")
  ),
  server = function(input, output) {
    output$data &lt;- renderTable({
      mtcars[, c("mpg", input$variable), drop = FALSE]
    }, rownames = TRUE)
  }
)

# demoing group support in the `choices` arg
shinyApp(
  ui = fluidPage(
    selectInput("state", "Choose a state:",
      list(`East Coast` = list("NY", "NJ", "CT"),
           `West Coast` = list("WA", "OR", "CA"),
           `Midwest` = list("MN", "WI", "IA"))
    ),
    textOutput("result")
  ),
  server = function(input, output) {
    output$result &lt;- renderText({
      paste("You chose", input$state)
    })
  }
)
}


library(shiny)
ui &lt;- fluidPage(
    mainPanel(
    htmlOutput("mytextbox"),
    uiOutput('inputs') 
    )
)
server &lt;- function(input, output){

    output$mytextbox &lt;- renderUI({
        HTML('&lt;textarea id="test" rows="3" cols="40"&gt;&lt;/textarea&gt;')
    })
    #render another UI
    output$inputs &lt;- renderUI({ 
        tagList(textInput('text_test', '', input$test),
                radioButtons('radio_bttn', 'First choice is the texarea content',
                             choices = c(input$test, 'another option')))
    })
}
shinyApp(ui, server)


